(function(jQuery){jQuery.fn.overlabel=function(targField){this.each(function(){var label=jQuery(this),field=targField||jQuery("#"+(label.data("target")||label.attr("for")));label.removeClass("overlabel").addClass("overlabel-apply").click(function(e){if(!field.attr("disabled")){field.focus()}e.preventDefault()});if(field.width()){label.width(field.width())}field.on({"focus":function(){label.addClass("hidden")},"blur change":function(){var $el=jQuery(this);if($el.val()===""){label.removeClass("hidden")}else{label.addClass("hidden")}}});if(field.val()&&field.val()!==""){label.addClass("hidden")}});return this}}(jQuery));